sp_angular_provider.script:
function mmpImageTiles() {
    var directive = {};
    directive.restrict = 'E';
    directive.scope = {
      imageTilesDetails: '='
    };
    directive.template= '<div class="container-fluid">'+
'    <slick infinite="false" slides-to-show="4" slides-to-scroll="1" responsive="[{breakpoint: 1024,settings:{slidesToShow: 3}},{breakpoint: 768,settings:{slidesToShow: 2}},{breakpoint: 480,settings:{slidesToShow: 1}}]">'+
'        <div ng-repeat="slide in imageTilesDetails.tiles">'+
'            <div class="travel-slide" style="background-image: url(\'{{slide.imageURL}}\');">'+
'                <a href="{{slide.URL}}">'+
'                    <div class="travel-slide-info">'+
'                        '+
'                        <h4>{{slide.name}}</h4>'+
'                        <span>{{slide.URLText}} <i class="fa fa-caret-right" aria-hidden="true"></i></span>'+
'                        '+
'                    </div>'+
'                </a>'+
'            </div>'+
'        </div>'+
'    </slick>'+
'    <a href="{{imageTilesDetails.seeAllHref}}" class="image-slider-see-more" ng-show="imageTilesDetails.seeAllEnabled">See More</a>'+
'</div>';
    return directive; 
}
