sp_angular_provider.script:
function mmpMajorTask()  {  
    var directive = {};
    directive.restrict = 'E';
    directive.scope = {
        title: '@',
        shortDescription: '@',
        action: '='
    };
    directive.template= '<div class="text-center major-task-dir-div">'+
'    <h3>{{title}}</h3>'+
'    <h4>{{shortDescription}}</h4>'+
'    <button class="btn {{action.btnClass}}" ng-click="action.clickAction" ng-if="action">{{action.name}}</button>'+
'</div>';
    return directive;
}
